command:
// Run this as a script macro in a world to set up the barbrawl defaults for that world, reset all actors' bars to that and set all places tokens to the new bars.
const mechBars = {
  bar1: {
    id: "bar1",
    ignoreMin: true,
    ignoreMax: false,
    mincolor: "#FF0000",
    maxcolor: "#80FF00",
    position: "bottom-inner",
    attribute: "derived.hp",
    visibility: CONST.TOKEN_DISPLAY_MODES.OWNER,
    indentLeft: 30,
    indentRight: null,
    shareHeight: true,
    label: "HP",
    style: "fraction",
  },

  structure: {
    id: "structure",
    attribute: "derived.structure",
    mincolor: "#1F9EFF",
    maxcolor: "#1F9EFF",
    position: "bottom-inner",
    attribute: "derived.structure",
    visibility: CONST.TOKEN_DISPLAY_MODES.ALWAYS,
    indentLeft: null,
    indentRight: 70,
    shareHeight: true,
    subdivisions: 4,
    subdivisionsMatchesMax: true,
    subdivisionsOwner: true,
  },
  stress: {
    id: "stress",
    mincolor: "#FF7B00",
    maxcolor: "#FF7B00",
    position: "bottom-outer",
    attribute: "derived.stress",
    visibility: CONST.TOKEN_DISPLAY_MODES.ALWAYS,
    indentLeft: null,
    indentRight: 70,
    shareHeight: true,
    subdivisions: 4,
    subdivisionsMatchesMax: true,
    subdivisionsOwner: true,
  },
  bar2: {
    id: "bar2",
    ignoreMax: true,
    ignoreMin: false,
    mincolor: "#700000",
    maxcolor: "#ff0000",
    position: "bottom-outer",
    attribute: "derived.heat",
    visibility: CONST.TOKEN_DISPLAY_MODES.OWNER,
    indentLeft: 30,
    indentRight: 0,
    shareHeight: true,
    label: "Heat",
    style: "fraction",
  },
  burn: {
    id: "burn",
    mincolor: "#992222",
    maxcolor: "#992222",
    position: "top-outer",
    attribute: "burn",
    visibility: CONST.TOKEN_DISPLAY_MODES.OWNER,
  },
  overshield: {
    id: "overshield",
    mincolor: "#222299",
    maxcolor: "#222299",
    position: "top-outer",
    attribute: "overshield",
    visibility: CONST.TOKEN_DISPLAY_MODES.OWNER,
  },
};

const pilotBars = {
  bar1: {
    id: "bar1",
    ignoreMin: true,
    ignoreMax: false,
    mincolor: "#FF0000",
    maxcolor: "#80FF00",
    position: "bottom-inner",
    attribute: "derived.hp",
    visibility: CONST.TOKEN_DISPLAY_MODES.OWNER,
    indentLeft: null,
    indentRight: null,
    shareHeight: true,
    label: "HP",
    style: "fraction",
  },
  overshield: {
    id: "overshield",
    mincolor: "#222299",
    maxcolor: "#222299",
    position: "top-outer",
    attribute: "overshield",
    visibility: CONST.TOKEN_DISPLAY_MODES.OWNER,
  },
};

const deployableBars = {
  bar1: {
    id: "bar1",
    ignoreMin: true,
    ignoreMax: false,
    mincolor: "#FF0000",
    maxcolor: "#80FF00",
    position: "bottom-inner",
    attribute: "derived.hp",
    visibility: CONST.TOKEN_DISPLAY_MODES.OWNER,
    indentLeft: null,
    indentRight: null,
    shareHeight: true,
    label: "HP",
    style: "fraction",
  },
  bar2: {
    id: "bar2",
    ignoreMax: true,
    ignoreMin: false,
    mincolor: "#700000",
    maxcolor: "#ff0000",
    position: "bottom-outer",
    attribute: "derived.heat",
    visibility: CONST.TOKEN_DISPLAY_MODES.OWNER,
    indentLeft: null,
    indentRight: null,
    shareHeight: true,
    label: "Heat",
    style: "fraction",
  },
  burn: {
    id: "burn",
    mincolor: "#992222",
    maxcolor: "#992222",
    position: "top-outer",
    attribute: "burn",
    visibility: CONST.TOKEN_DISPLAY_MODES.OWNER,
  },
  overshield: {
    id: "overshield",
    mincolor: "#222299",
    maxcolor: "#222299",
    position: "top-outer",
    attribute: "overshield",
    visibility: CONST.TOKEN_DISPLAY_MODES.OWNER,
  },
};

const npcBars = mechBars;

await game.settings.set("barbrawl", "defaultsPerType", true);

const barConfig = game.settings.get("barbrawl", "defaultResources") ?? {};
barConfig['mech'] = mechBars;
barConfig['npc'] = npcBars;
barConfig['pilot'] = pilotBars;
barConfig['deployable'] = deployableBars;
await game.settings.set("barbrawl", "defaultTypeResources", barConfig);

// :warning: Reset all actors' prototype token bars
await Promise.all(game.actors.map(a => a.update({ "token.flags.barbrawl.-=resourceBars": null })));

// Reset the bars on all existing tokens
await Promise.all(
  game.scenes.map(s => {
    const updates = s.tokens.filter(t => t.actor).map(t => {
      let barSettings = mechBars;
      console.log(t.actor.type);
      switch (t.actor.type) {
        case 'npc':
          barSettings = npcBars;
          break;
        case 'pilot':
          barSettings = pilotBars;
          break;
        case 'deployable':
          barSettings = deployableBars;
          break;

        default:
          break;
      }
      return {
        _id: t.id,
        "flags.barbrawl.resourceBars": barSettings,
      };
    });
    return s.updateEmbeddedDocuments("Token", updates);
  })
);

ui.notifications.info("Done");
// vim:ft=javascript:
